import React, { Component } from "react";
//import './login.css';

export const authEndpoint = 'https://accounts.spotify.com/authorize';
// Replace with your app's client ID, redirect URI and desired scopes
const clientId = process.env.CLIENT_ID;
const redirectUri = process.env.REDIRECT_URI;
const scopes = [
  "user-read-currently-playing",
  "user-read-playback-state",
];
export const spotifyWebApiURL = `https://accounts.spotify.com/authorize/?client_id=${clientId
}&response_type=token&redirect_uri=${redirectUri}&scope=${scopes}`;

class Login extends Component {

  constructor(props) {
    super(props);
    this.state = {
        access_token: ''
    }
}
componentDidMount = () => {
  let url = window.location.href
  if(url.indexOf('_token')>-1){            
      let access_token = url.split('_token=')[1].split("&")[0].trim()
      this.setState({ access_token })
  }
}

makeSpotifyCall = (event) => {
  event.preventDefault()
  const { access_token } = this.state
  if(access_token===''){
      document.location = spotifyWebApiURL
  }else{
      Router.push({
          pathname: '/user',
          query: { access_token }
      })
  }  
}


  render() {

    const styles = {
      border: "0.2em solid #1ecd97",
      textAlign: "center",
      display: "inline-block",
      fontSize: "16px",
      backgroundColor: "transparent",
      borderRadius: "2em",
      color: "#1ecd97",
      cursor: "pointer",
      fontSize: "3vmin",
      padding: "0.7em 1.5em",
      textTransform: "uppercase",
      transition: "all 0.25s ease",
    }

    const { access_token } = this.state

    return (
      <div className="Login" style={{textAlign: "center"}}>
        < Header />
      </div>
    );
  }
}

export default Login;
