import React from "react";

class Input extends React.Component {
  constructor() {
    super();
    this.state = {
      score: 0
    };
  }

  incrementScore() {
    this.setState = prev => {
      return {
        score: prev.score + 1
      };
    };
  }

  render() {
    return (
      <div>
        <h1>The input element</h1>
        <form>
          <label for="sname">Song Name</label>
          <input type="text" id="sname" name="sname"></input>
        </form>

        <button onClick={this.incrementScore}>thumbs up</button>
        <button>thumbs down</button>
        {this.state.score}

        <p>
          Click the "Submit" button and the form-data will be sent to a page on
          the server called "action_page.php".
        </p>
      </div>
    );
  }
}

export default Input;
