import React from "react";
import { useState, useCallback } from "react";
import useSWR from "swr";
import fetch from "isomorphic-unfetch";
import "./style.css";
import Retrieve from "./Retrieve";

function Input_Fun() {
  // handles changes to name of song dynamically
  const submit = useCallback(async event => {
    await fetch("/api/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      // the body of this song is built from state
      body: JSON.stringify({
        song: name, // ISSUE 1: how to access state variables inside this hook?
        score: score
      })
    });

    await fetch("/api/all")
      .then(response => {
        console.log("Response: " + response.json());
      })
      .then(data => {
        console.log("DATA: " + data);
      });

    // ISSUE 2: how to call this hook inside another hook? I know
    // you can't do this but what might be a good logical work-around?

    // const { data, error } = useSWR("/api/all", fetch, {
    //   // see example repo for explination about booleans
    //   revalidateOnFocus: false,
    //   revalidateOnReconnect: false
    // });

    const newSongs = {
      result: [
        { _id: "s_0", song: { song: "s_0", score: 0 } },
        { _id: "s_1", song: { song: "s_1", score: 1 } },
        { _id: "s_2", song: { song: "s_2", score: 2 } },
        { _id: "s_3", song: { song: "s_3", score: 5 } },
        { _id: "s_4", song: { song: "s_4", score: 4 } },
        { _id: "s_5", song: { song: "s_5", score: 5 } },
        { _id: "s_6", song: { song: "s_6", score: 6 } }
      ]
    };

    // update sent
    setSent(true);
    setData(newSongs);
  }, []);

  // set initial hooks to keep track of state
  const [score, setScore] = useState(0);
  const [name, setName] = useState("");
  const [sentDatabase, setSent] = useState(false);
  const [data, setData] = useState({});

  return (
    <div>
      {/* Gather name of song */}
      <form>
        <label form="sname">Song Name </label>
        <input
          type="text"
          id="sname"
          name="sname"
          value={name}
          onChange={() => setName(event.target.value)}
          placeholder="enter song name"
        ></input>
      </form>
      {/* Gather score of song */}
      <h1>{score}</h1>
      <button onClick={() => setScore(score + 1)}>Upvote</button>
      <button onClick={() => setScore(score - 1)}>Downvote</button>
      <br />
      <br />
      {/* sumbit name and score of song to MongoDB Database*/}
      <button
        onClick={() => submit()}
        className="button"
        style={{ verticalAlign: "middle" }}
      >
        {" "}
        <span> Save to Database </span>
      </button>
      {/* Conditional rendering to display data sent to MongoDB Database*/}
      <h1 style={{ display: sentDatabase ? "block" : "none" }}>
        {" "}
        {name} saved to MongoDB Database{" "}
      </h1>
      <Retrieve data={data} />
    </div>
  );
}

export default Input_Fun;
